class DotLottiePlayer extends LitElement {
    constructor() {
        super(...arguments);
        this.background = 'transparent';
        this.controls = false;
        this.currentState = PlayerState.Loading;
        this.direction = 1;
        this.hover = false;
        this.intermission = 0;
        this.loop = false;
        this.mode = PlayMode.Normal;
        this.objectfit = 'contain';
        this.renderer = 'svg';
        this.speed = 1;
        this.subframe = false;
        this._lottie = null;
        this._counter = 0;
    }
    async load(src) {
        if (!this.shadowRoot) {
            return;
        }
        const preserveAspectRatio = this.preserveAspectRatio ?? (this.objectfit && aspectRatio(this.objectfit)), options = {
            container: this.container,
            loop: !!this.loop,
            autoplay: !!this.autoplay,
            renderer: this.renderer,
            initialSegment: this.segment,
            rendererSettings: {
                imagePreserveAspectRatio: preserveAspectRatio,
            }
        };
        switch (this.renderer) {
            case 'svg':
                options.rendererSettings = {
                    ...options.rendererSettings,
                    hideOnTransparent: true,
                    preserveAspectRatio,
                    progressiveLoad: true,
                };
                break;
            case 'canvas':
                options.rendererSettings = {
                    ...options.rendererSettings,
                    clearCanvas: true,
                    preserveAspectRatio,
                    progressiveLoad: true,
                };
                break;
            case 'html':
                options.rendererSettings = {
                    ...options.rendererSettings,
                    hideOnTransparent: true
                };
        }
        try {
            if (typeof src !== 'string' && typeof src !== 'object') {
                throw new Error('No Lottie animation to load, or the file is corrupted.');
            }
            const srcParsed = typeof src === 'string' ? await fetchPath(src) : src;
            if (!this.isLottie(srcParsed))
                throw new Error('dotLottie: Load method failed. Object is not a valid Lottie.');
            if (this._lottie)
                this._lottie.destroy();
            this._lottie = Lottie.loadAnimation({
                ...options,
                animationData: srcParsed,
            });
        }
        catch (err) {
            console.error(err);
            this.currentState = PlayerState.Error;
            this.dispatchEvent(new CustomEvent(PlayerEvents.Error));
            return;
        }
        if (this._lottie) {
            this._lottie.addEventListener('enterFrame', () => {
                const { currentFrame, totalFrames } = this._lottie;
                this.seeker = (currentFrame / totalFrames) * 100;
                this.dispatchEvent(new CustomEvent(PlayerEvents.Frame, {
                    detail: {
                        frame: currentFrame,
                        seeker: this.seeker,
                    },
                }));
            });
            this._lottie.addEventListener('complete', () => {
                this.currentState = PlayerState.Completed;
                this.dispatchEvent(new CustomEvent(PlayerEvents.Complete));
            });
            const _loopComplete = () => {
                const { firstFrame, totalFrames, playDirection, } = this._lottie;
                if (this.count) {
                    this.mode === PlayMode.Bounce ?
                        this._counter += 1 : this._counter += 0.5;
                    if (this._counter >= this.count) {
                        this.setLooping(false);
                        this.currentState = PlayerState.Completed;
                        this.dispatchEvent(new CustomEvent(PlayerEvents.Complete));
                        return;
                    }
                }
                this.dispatchEvent(new CustomEvent(PlayerEvents.Loop));
                if (this.mode === PlayMode.Bounce) {
                    this._lottie?.goToAndStop(playDirection === -1 ? firstFrame : totalFrames * .99, true);
                    this._lottie?.setDirection(playDirection * -1);
                    return setTimeout(() => {
                        this._lottie?.play();
                    }, this.intermission);
                }
                this._lottie?.goToAndStop(playDirection === -1 ? totalFrames * .99 : firstFrame, true);
                return setTimeout(() => {
                    this._lottie?.play();
                }, this.intermission);
            };
            this._lottie.addEventListener('loopComplete', _loopComplete);
            this._lottie.addEventListener('DOMLoaded', () => {
                this.dispatchEvent(new CustomEvent(PlayerEvents.Ready));
            });
            this._lottie.addEventListener('data_ready', () => {
                this.dispatchEvent(new CustomEvent(PlayerEvents.Load));
            });
            this._lottie.addEventListener('data_failed', () => {
                this.currentState = PlayerState.Error;
                this.dispatchEvent(new CustomEvent(PlayerEvents.Error));
            });
            if (this.container) {
                this.container.addEventListener('mouseenter', () => {
                    if (this.hover && this.currentState !== PlayerState.Playing) {
                        this.play();
                    }
                });
                this.container.addEventListener('mouseleave', () => {
                    if (this.hover && this.currentState === PlayerState.Playing) {
                        this.stop();
                    }
                });
            }
            this.setSpeed(this.speed);
            this.setDirection(this.direction);
            this.setSubframe(!!this.subframe);
            console.log(this._lottie.isSubframeEnabled);
            if (this.autoplay) {
                if (this.direction === -1)
                    this.seek('99%');
                this.play();
            }
        }
    }
    _onVisibilityChange() {
        if (document.hidden && this.currentState === PlayerState.Playing) {
            this.freeze();
        }
        else if (this.currentState === PlayerState.Frozen) {
            this.play();
        }
    }
    _handleSeekChange(event) {
        if (!event.target || !this._lottie || isNaN(Number(event.target.value)))
            return;
        const frame = (Number(event.target.value) / 100) * this._lottie.totalFrames;
        this.seek(frame);
    }
    isLottie(json) {
        const mandatory = ['v', 'ip', 'op', 'layers', 'fr', 'w', 'h'];
        return mandatory.every((field) => Object.prototype.hasOwnProperty.call(json, field));
    }
    getLottie() {
        return this._lottie;
    }
    play() {
        if (!this._lottie)
            return;
        this.currentState = PlayerState.Playing;
        this._lottie.play();
        this.dispatchEvent(new CustomEvent(PlayerEvents.Play));
    }
    pause() {
        if (!this._lottie)
            return;
        this.currentState = PlayerState.Paused;
        this._lottie.pause();
        this.dispatchEvent(new CustomEvent(PlayerEvents.Pause));
    }
    stop() {
        if (!this._lottie)
            return;
        this.currentState = PlayerState.Stopped;
        this._counter = 0;
        this._lottie.stop();
        this.dispatchEvent(new CustomEvent(PlayerEvents.Stop));
    }
    destroy() {
        if (!this._lottie)
            return;
        this.currentState = PlayerState.Destroyed;
        this._lottie.destroy();
        this._lottie = null;
        this.dispatchEvent(new CustomEvent(PlayerEvents.Destroyed));
        this.remove();
    }
    seek(value) {
        if (!this._lottie)
            return;
        const matches = value.toString().match(/^([0-9]+)(%?)$/);
        if (!matches) {
            return;
        }
        const frame = matches[2] === '%' ? (this._lottie.totalFrames * Number(matches[1])) / 100 : matches[1];
        this.seeker = Number(frame);
        if (this.currentState === PlayerState.Playing) {
            this._lottie.goToAndPlay(frame, true);
        }
        else {
            this._lottie.goToAndStop(frame, true);
            this._lottie.pause();
        }
    }
    snapshot(download = true) {
        if (!this.shadowRoot)
            return;
        const svgElement = this.shadowRoot.querySelector('.animation svg');
        const data = svgElement instanceof Node ? new XMLSerializer().serializeToString(svgElement) : null;
        if (!data)
            return;
        if (download) {
            const element = document.createElement('a');
            element.href = 'data:image/svg+xmlcharset=utf-8,' + encodeURIComponent(data);
            element.download = 'download_' + this.seeker + '.svg';
            document.body.appendChild(element);
            element.click();
            document.body.removeChild(element);
        }
        return data;
    }
    setSubframe(value) {
        if (!this._lottie)
            return;
        this.subframe = value;
        this._lottie.setSubframe(value);
    }
    freeze() {
        if (!this._lottie)
            return;
        this.currentState = PlayerState.Frozen;
        this._lottie.pause();
        this.dispatchEvent(new CustomEvent(PlayerEvents.Freeze));
    }
    async reload() {
        if (!this._lottie)
            return;
        this._lottie.destroy();
        if (this.src) {
            await this.load(this.src);
        }
    }
    setSpeed(value = 1) {
        if (!this._lottie)
            return;
        this.speed = value;
        this._lottie.setSpeed(value);
    }
    setDirection(value) {
        if (!this._lottie)
            return;
        this.direction = value;
        this._lottie.setDirection(value);
    }
    setLooping(value) {
        if (this._lottie) {
            this.loop = value;
            this._lottie.setLoop(value);
        }
    }
    togglePlay() {
        if (!this._lottie)
            return;
        const { currentFrame, playDirection, totalFrames } = this._lottie;
        if (this.currentState === PlayerState.Playing)
            return this.pause();
        if (this.currentState === PlayerState.Completed) {
            this.currentState = PlayerState.Playing;
            if (this.mode === PlayMode.Bounce) {
                this.setDirection(playDirection * -1);
                return this._lottie.goToAndPlay(currentFrame, true);
            }
            if (playDirection === -1) {
                return this._lottie.goToAndPlay(totalFrames, true);
            }
            return this._lottie.goToAndPlay(0, true);
        }
        return this.play();
    }
    toggleLooping() {
        this.setLooping(!this.loop);
    }
    toggleBoomerang() {
        if (this.mode === PlayMode.Normal) {
            this.mode = PlayMode.Bounce;
        }
        else {
            this.mode = PlayMode.Normal;
        }
    }
    static get styles() {
        return styles;
    }
    connectedCallback() {
        super.connectedCallback();
        if (typeof document.hidden !== 'undefined') {
            document.addEventListener('visibilitychange', this._onVisibilityChange);
        }
    }
    async firstUpdated() {
        if ('IntersectionObserver' in window) {
            this._io = new IntersectionObserver((entries) => {
                if (entries[0].isIntersecting) {
                    if (this.currentState === PlayerState.Frozen) {
                        this.play();
                    }
                }
                else if (this.currentState === PlayerState.Playing) {
                    this.freeze();
                }
            });
            this._io.observe(this.container);
        }
        if (this.src) {
            await this.load(this.src);
        }
        this.dispatchEvent(new CustomEvent(PlayerEvents.Rendered));
    }
    disconnectedCallback() {
        super.disconnectedCallback();
        if (this._io) {
            this._io.disconnect();
            this._io = undefined;
        }
        if (this._lottie)
            this._lottie.destroy();
        document.removeEventListener('visibilitychange', this._onVisibilityChange);
    }
    renderControls() {
